project.scriptdef:







var correctedInputPath = attributes.get("inputpath").replace("\\", "/");
var correctedPrefix = attributes.get("prefix").replace("\\", "/");
var index = correctedInputPath.indexOf(correctedPrefix);

var result = "";
if (index >= 0) {
  var suffix = correctedInputPath.substring(index + correctedPrefix.length());
  if (suffix.charAt(0) == 47) {
      suffix = suffix.substring(1);
  }

  var i = 0;
  for (i = 0; i < suffix.length(); i++) {
      if (suffix.charAt(i) == 47) {
          result = result.concat("../");
      }
  }
}
project.setProperty(attributes.get("resultproperty"), result);
